const createPlayerCards = function (player) {
  const card = document.createElement("div");
  card.classList.add("card");
  card.classList.add("flex");
  card.classList.add("gap1");
  card.id = player.id;

  const avatar = document.createElement("div");
  avatar.classList.add("avatar");
  avatar.innerHTML = player.marker;

  const playerName = document.createElement("input");
  playerName.type = "text";
  playerName.value = player.name;
  playerName.classList.add("player-name");

  playerName.addEventListener("change", () => {
    console.log(`${player.name} is now known as ${playerName.value}`);
    player.name = playerName.value;
  });

  const playerColor = document.createElement("div");
  playerColor.classList.add("player-color");

  const colorPicker = document.createElement("input");
  colorPicker.type = "color";
  colorPicker.value = player.color;
  colorPicker.classList.add("color-picker");
  playerColor.appendChild(colorPicker);

  colorPicker.addEventListener("change", () => {
    console.log(`${player.name} chose a new color ${colorPicker.value}`);
    player.color = colorPicker.value;
  });

  card.appendChild(avatar);
  card.appendChild(playerName);
  card.appendChild(playerColor);

  const dialogBody = document.createElement("dialog");
  dialogBody.classList.add("pick-avatar");

  const avatarGrid = document.createElement("div");
  avatarGrid.classList.add("grid");
  avatarGrid.classList.add("dialog-grid");
  AVATARS.forEach((avt) => {
    let optionWrapper = document.createElement("div");
    optionWrapper.classList.add("option-wrapper");
    optionWrapper.id = avt.id;
    let option = document.createElement("div");
    option.classList.add("dialog-choice");
    option.innerHTML = avt.svg;
    optionWrapper.appendChild(option);
    avatarGrid.appendChild(optionWrapper);
  });
  dialogBody.appendChild(avatarGrid);
  card.appendChild(dialogBody);

  const parent = document.querySelector(".info");
  parent.appendChild(card);

  avatar.addEventListener("click", () => {
    avatarGrid.addEventListener("click", (e) => {
      console.log(
        `${player.name} chose avatar ${e.target.closest(".option-wrapper").id}`
      );
      const newAvatar = AVATARS.find(
        (item) => item.id === e.target.closest(".option-wrapper").id
      ).svg;
      player.marker = newAvatar;
      avatar.innerHTML = newAvatar;
      dialogBody.close();
    });

    dialogBody.showModal();
  });
};

